  // console.log('ctx', ctx.ctx);
  const { req, query } = ctx.ctx;
  console.log('query', ctx.ctx.query);
  const promise = new Promise((resolve) => {
    setTimeout(() => {
      resolve({
        query,
        name: 'jock lili',
      });
    }, 1000);
  });
  const ret = await promise;
  return ret;
  // return ({ query, name: 'jocky' });
};

export default withRouter(A);

