import { Button } from 'antd';
import LINK from 'next/link';
import Router from 'next/router';

const events = [
  'routeChangeStart',
  'routeChangeComplete',
  'beforeHistoryChange',
  'routeChangeError',
  'hashChangeStart',
  'hashChangeComplete',
];

const makeEvent = (type) => (...args) => { console.log(type, ...args); };

events.forEach((event) => {
  Router.events.on(event, makeEvent(event));
});

export default () => (
  <div>
    <LINK href="/a?id=1" as="/a/1">
      <Button>to A</Button>
    </LINK>
    <LINK href="#aaa">
      <Button>hash</Button>
    </LINK>
    <Button onClick={() => { Router.push('/test/b'); }}>to test/b</Button>
    <Button onClick={() => {
      Router.push({
        pathname: '/test/b',
        query: {
          id: 2,
        },
      }, '/test/b/2');
    }}>to test/b</Button>
  </div>
);
